options.css:
.button {
  cursor: pointer;
  font-size: 1.8rem;
  border-radius: 30px;
  border: none;
  padding: 1px;
  background: radial-gradient(circle 100px at 80% -10%, #ffffff, #181b1b);
  position: relative;
}
.button::after {
  content: "";
  position: absolute;
  width: 75%;
  height: 70%;
  border-radius: 150px;
  top: 0;
  right: 0;
  box-shadow: 0 0 50px #ffffff38;
  z-index: -1;
}

.blob1 {
  position: absolute;
  width: 200px;
  height: 100%;
  border-radius: 20px;
  bottom: 0;
  left: 0;
  background: radial-gradient(
    circle 100px at 10% 50%,
    #3fe9ff,
    #003641,
    transparent
  );
  box-shadow: -10px 10px 50px #003641;
}

.inner {
  padding: 20px 200px;
  border-radius: 25px;
  color: #f8ece9;
  z-index: 3;
  position: relative;
  background: radial-gradient(circle 150px at 80% -80%, #003641, #0f1111);
}
.inner::before {
  content: "";
  width: 100%;
  height: 100%;
  left: 0;
  top: 0;
  border-radius: 10px;
  background: radial-gradient(
    circle 60px at 0% 100%,
    #00e1ff1a,
    #0000ff11,
    transparent
  );
  position: absolute;
}

options.onInit:
// Sets the text from customProperties
const htmlgraphicsText = htmlNode.getElementById('htmlgraphics-text');

if (htmlgraphicsText) {
  htmlgraphicsText.textContent = customProperties.text;

  // Change the text color based on the theme
  if (theme.isDark) {
    htmlgraphicsText.style.color = 'green';
  } else {
    htmlgraphicsText.style.color = 'red';
  }
}

options.onRender:
// Sets the value from the first series on every refresh
const htmlgraphicsValue = htmlNode.getElementById('htmlgraphics-value');

if (htmlgraphicsValue) {
  const valueField = data.series[0]?.fields[1];
  if (valueField) {
    const length = valueField.values.length;
    htmlgraphicsValue.textContent = valueField.values.get(length - 1);
  } else {
    htmlgraphicsValue.textContent = "No data"
  }
}

